import React, { useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, CardMedia, Button, List, ListItem, ListItemText, Box } from '@mui/material';

const PersonalizedRecommendations = () => {
  const [shoppingList, setShoppingList] = useState([]);

  // Sample recommendations; replace this with real data from your application
  const recommendations = [
    {
      title: 'Veggie Stir Fry',
      description: 'A delicious recipe that uses up your veggies.',
      image: 'data:image/jpeg;base64,',
      link: 'https://www.google.com/search?q=Veggie+Stir+Fry+recipe', // Link to Google search
      ingredients: ['Bell Peppers', 'Carrots', 'Broccoli', 'Soy Sauce']
    },
    {
      title: 'Chicken Salad',
      description: 'Quick and easy meal for busy days.',
      image: 'data:image/jpeg;base64,',
      link: 'https://www.google.com/search?q=Chicken+Salad+recipe', // Link to Google search
      ingredients: ['Chicken Breast', 'Lettuce', 'Tomatoes', 'Olive Oil']
    },
    {
      title: 'Pasta Primavera',
      description: 'Use your leftover ingredients in this dish.',
      image: 'https://images.themodernproper.com/billowy-turkey/production/posts/PastaPrimavera_10.jpg?w=1200&h=1200&q=60&fm=jpg&fit=crop&dm=1719193287&s=0104e0b241aea73e5709db128503d749',
      link: 'https://www.google.com/search?q=Pasta+Primavera+recipe', // Link to Google search
      ingredients: ['Pasta', 'Zucchini', 'Cherry Tomatoes', 'Parmesan Cheese']
    }
  ];

  const handleAddToShoppingList = (ingredients) => {
    setShoppingList((prevList) => [...prevList, ...ingredients]);
  };

  const uniqueIngredients = [...new Set(shoppingList)];

  return (
    <Container>
      <Typography variant="h5" gutterBottom>
        Personalized Recommendations
      </Typography>
      <Grid container spacing={2}>
        {recommendations.map((rec, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card sx={{ borderRadius: 2, boxShadow: 3 }}>
              <CardMedia
                component="img"
                height="140"
                image={rec.image}
                alt={rec.title}
                sx={{ borderTopLeftRadius: 2, borderTopRightRadius: 2 }}
              />
              <CardContent>
                <Typography variant="h6" sx={{ fontWeight: 'bold' }}>{rec.title}</Typography>
                <Typography variant="body2" color="text.secondary">{rec.description}</Typography>
                <Box mt={2}>
                  <Button size="small" color="primary" href={rec.link} target="_blank">
                    View Recipe
                  </Button>
                  <Button 
                    size="small" 
                    color="secondary" 
                    onClick={() => handleAddToShoppingList(rec.ingredients)}
                    sx={{ ml: 1 }} // Material-UI spacing
                  >
                    Add to Shopping List
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h5" gutterBottom sx={{ marginTop: '20px' }}>
        Smart Shopping List
      </Typography>
      <List sx={{ border: '1px solid #ccc', borderRadius: '8px', padding: '10px' }}>
        {uniqueIngredients.length > 0 ? (
          uniqueIngredients.map((item, index) => (
            <ListItem key={index}>
              <ListItemText primary={item} />
            </ListItem>
          ))
        ) : (
          <ListItem>
            <ListItemText primary="Your shopping list is empty." />
          </ListItem>
        )}
      </List>
    </Container>
  );
};

export default PersonalizedRecommendations;
